import React, { useEffect } from "react";
import styled from "styled-components";
import { InnerLayout } from "../../Styles/Layouts";
import { useGlobalContext } from "../../Context/globalContext";
import Form from '../Form/Form';
import ExpensesItem from "../ExpensesItem/ExpensesItem";

function Expenses() {
    const {addExpenses, Expensess, getExpensess, deleteExpenses, totalExpenses} = useGlobalContext()

    useEffect(() => {
        getExpensess()
    }, [])
    return (
        <ExpensesStyled>
            <InnerLayout>
                <h1>Expenses</h1>
                <h2 className="total-Expenses">Total Expenses: <span>${totalExpenses()}</span></h2>
                <div className="Expenses-content">
                    <div className="form-container">
                        <Form />
                    </div>
                <div className="Expensess">
                    {Expensess.map((Expenses) => {
                        const { _id, title, amount, date, category, description } = Expenses;
                        return <ExpensesItem 
                            key={_id}
                            id={_id}
                            title={title}
                            description={description}
                            amount={amount}
                            date={date}
                            category={category}
                            indicatorColor="var(--color-green)"
                            deleteItem={deleteExpenses}
                        />
                    })}
                </div>
                </div>
            </InnerLayout>
        </ExpensesStyled>
    )
}

const ExpensesStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-Expenses{
        display: flex;
        justify-content: center;
        align-items: center;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    .Expenses-content{
        display: flex;
        gap: 2rem;
        .Expensess{
            flex: 1;
        }
    }
`;

export default Expenses
